import React, { useState } from 'react';
import { Button, Form, Container, Row, Col, Alert } from 'react-bootstrap';
import axios from 'axios';
import './dialer.css';

const Dialer = () => {
  const [filePath, setFilePath] = useState('');
  const [fromNumber, setFromNumber] = useState('');
  const [error, setError] = useState('');
  const [isCalling, setIsCalling] = useState(false);

  const handleCall = () => {
    if (filePath && fromNumber) {
      setError('');
      setIsCalling(true);
      axios.post('http://localhost:5000/make-calls', {
        filePath: filePath,
        fromNumber: fromNumber,
        limit: 50 // Adding limit parameter for 50 calls
      })
      .then(response => {
        console.log(response.data);
        setIsCalling(false);
      })
      .catch(error => {
        console.error('Error making calls:', error);
        setError('Error making calls.');
        setIsCalling(false);
      });
    } else {
      setError('Please provide both the file path and Twilio number.');
    }
  };

  const handle50Calls = () => {
    if (filePath && fromNumber) {
      setError('');
      setIsCalling(true);
      axios.post('http://localhost:5000/make-calls', {
        filePath: filePath,
        fromNumber: fromNumber,
        limit: 50
      })
      .then(response => {
        console.log(response.data);
        setIsCalling(false);
      })
      .catch(error => {
        console.error('Error making calls:', error);
        setError('Error making calls.');
        setIsCalling(false);
      });
    } else {
      setError('Please provide both the file path and Twilio number.');
    }
  };

  return (
    <Container className="dialer-container">
      <Row className="justify-content-center">
        <Col md={6}>
          <div className="card p-4 shadow">
            <h2 className="text-center mb-4">Virtual Number Caller</h2>
            {error && <Alert variant="danger">{error}</Alert>}
            <Form>
              <Form.Group controlId="formFilePath">
                <Form.Label>Excel File Path</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter path to Excel file"
                  value={filePath}
                  onChange={(e) => setFilePath(e.target.value)}
                />
              </Form.Group>
              <Form.Group controlId="formFromNumber" className="mt-3">
                <Form.Label>Twilio Virtual Number</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter Twilio virtual number"
                  value={fromNumber}
                  onChange={(e) => setFromNumber(e.target.value)}
                />
              </Form.Group>
              <div className="mt-4 text-center">
                <Button
                  variant="primary"
                  className="mx-2"
                  onClick={handleCall}
                  disabled={isCalling}
                >
                  {isCalling ? 'Calling...' : 'Call'}
                </Button>
                <Button
                  variant="success"
                  className="mx-2"
                  onClick={handle50Calls}
                  disabled={isCalling}
                >
                  {isCalling ? 'Calling...' : '50 Calls'}
                </Button>
              </div>
            </Form>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default Dialer;
