import React, { useState } from 'react';
import { Button, Form, Container, Row, Col, Alert } from 'react-bootstrap';
import './dialer.css';

const Dialer = () => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [virtualNumber, setVirtualNumber] = useState('');
  const [isCalling, setIsCalling] = useState(false);
  const [isRecording, setIsRecording] = useState(false);
  const [error, setError] = useState('');

  const handleCall = async () => {
    if (phoneNumber && virtualNumber) {
      setIsCalling(true);
      setError('');
      setMessage('');

      try {
        const response = await fetch('http://localhost:5000/api/call', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ phoneNumber, virtualNumber }),
        });

        const data = await response.json();
        if (response.ok) {
          setMessage(data.message);
        } else {
          setError(data.error);
        }
      } catch (err) {
        setError('An error occurred while making the call.');
      } finally {
        setIsCalling(false);
      }
    } else {
      setError('Please enter both phone number and virtual number.');
    }
  };

  const handleRecord = () => {
    if (isCalling) {
      setIsRecording(!isRecording);
      setError('');
    } else {
      setError('You must be on a call to start recording.');
    }
  };

  return (
    <Container className="dialer-container">
      <Row className="justify-content-center">
        <Col md={6}>
          <div className="card p-4 shadow">
            <h2 className="text-center mb-4">Virtual Number Caller</h2>
            {error && <Alert variant="danger">{error}</Alert>}
            <Form>
              <Form.Group controlId="formVirtualNumber">
                <Form.Label>Virtual Number</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter virtual number"
                  value={virtualNumber}
                  onChange={(e) => setVirtualNumber(e.target.value)}
                />
              </Form.Group>
              <Form.Group controlId="formPhoneNumber" className="mt-3">
                <Form.Label>Phone Number</Form.Label>
                <Form.Control
                  type="tel"
                  placeholder="Enter phone number"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                />
              </Form.Group>
              <div className="mt-4 text-center">
                <Button
                  variant="primary"
                  className="mx-2"
                  onClick={handleCall}
                  disabled={isCalling}
                >
                  {isCalling ? 'Calling...' : 'Call'}
                </Button>
                <Button
                  variant="secondary"
                  className="mx-2"
                  onClick={handleRecord}
                  disabled={!isCalling}
                >
                  {isRecording ? 'Stop Recording' : 'Record Call'}
                </Button>
              </div>
              {isCalling && (
                <div className="call-status mt-3">
                  <h5>Status: {isRecording ? 'Recording...' : 'In Call'}</h5>
                </div>
              )}
            </Form>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default Dialer;
